//! Method for differential literary analysis.
//! It wraps two methods that will be run after each other with the same input.
//! In comparison to the [`crate::methods::CombinedMethod`] it also runs the secondary method in `run_target`.
//!
use core::{cell::UnsafeCell, fmt::Debug, ptr};

use libafl_bolts::{ownedref::OwnedMutPtr, tuples::MatchName};
use serde::{Deserialize, Serialize};

use crate::{
    methods::{Method, ExitKind, HasObservers},
    inputs::UsesInput,
    observers::{DifferentialObserversTuple, ObserversTuple, UsesObservers},
    state::UsesState,
    Error,
};

/// A [`DiffMethod`] wraps a primary method, forwarding its methods, and a secondary one
#[derive(Debug)]
pub struct DiffMethod<A, B, OTA, OTB, DOT> {
    primary: A,
    secondary: B,
    observers: UnsafeCell<ProxyObserversTuple<OTA, OTB, DOT>>,
}

